import React, { useState } from "react";
// import ResultPage from "./ResultPage";

export const VehicleSelection = ({ cop, onCapture }) => {
  const [selectedVehicle, setSelectedVehicle] = useState("");

  const handleVehicleSelection = () => {
    // Simulate capture success if a vehicle is selected
    const captureSuccess = !!selectedVehicle;

    // Call the onCapture callback function with capture status and cop's name
    onCapture(captureSuccess ? cop : null);
  };

  const vehicles = [
    { kind: "EV Bike", range: 60, count: 2 },
    { kind: "EV Car", range: 100, count: 1 },
    { kind: "EV SUV", range: 120, count: 1 },
  ];

  return (
    <div>
      <h2>Vehicle Selection for Cop {cop}</h2>
      <form action="">
        <h2>City Selection for Cop 1</h2>
        <select value={cop1City} onChange={handleCop1CitySelection}>
          <option value="">Select City</option>
          {getCitiesForCop1().map((city) => (
            <option key={city.name} value={city.name}>
              {city.name}
            </option>
          ))}
        </select>
        <h2>City Selection for Cop 2</h2>
        <select value={cop2City} onChange={handleCop2CitySelection}>
          <option value="">Select City</option>
          {getCitiesForCop2().map((city) => (
            <option key={city.name} value={city.name}>
              {city.name}
            </option>
          ))}
        </select>
        <h2>City Selection for Cop 3</h2>
        <select value={cop3City} onChange={handleCop3CitySelection}>
          <option value="">Select City</option>
          {getCitiesForCop3().map((city) => (
            <option key={city.name} value={city.name}>
              {city.name}
            </option>
          ))}
        </select>
        <button type="submit">Capture</button>
      </form>
    </div>
  );
};
