import React, { useState } from "react";

export const CitySelection = () => {
  const cities = [
    { name: "Yapkashnagar", distance: 60 },
    { name: "Lihaspur", distance: 50 },
    { name: "Narmis City", distance: 40 },
    { name: "Shekharvati", distance: 30 },
    { name: "Nuravgram", distance: 20 },
  ];

  const [cop1City, setCop1City] = useState("");
  const [cop2City, setCop2City] = useState("");
  const [cop3City, setCop3City] = useState("");

  // Define available city options based on the selected cities
  const availableCitiesCop1 = getCitiesForCop1();
  const availableCitiesCop2 = getCitiesForCop2();
  const availableCitiesCop3 = getCitiesForCop3();

  function getCitiesForCop1() {
    return cities.filter(
      (city) => city.name !== cop2City && city.name !== cop3City
    );
  }

  function getCitiesForCop2() {
    return cities.filter(
      (city) => city.name !== cop1City && city.name !== cop3City
    );
  }

  function getCitiesForCop3() {
    return cities.filter(
      (city) => city.name !== cop1City && city.name !== cop2City
    );
  }

  const handleCop1CitySelection = (e) => {
    const selectedCity = e.target.value;
    setCop1City(selectedCity);
  };

  // Handler for Cop 2 city selection
  const handleCop2CitySelection = (e) => {
    const selectedCity = e.target.value;
    setCop2City(selectedCity);
  };

  // Handler for Cop 3 city selection
  const handleCop3CitySelection = (e) => {
    const selectedCity = e.target.value;
    setCop3City(selectedCity);
  };

  return (
    <div>
      <form action="">
        <h2>City Selection for Cop 1</h2>

        <select value={cop1City} onChange={(e) => handleCop1CitySelection}>
          <option value="">Select City</option>
          {getCitiesForCop1().map((city) => (
            <option key={city} value={city}>
              {city}
            </option>
          ))}
        </select>

        <h2>City Selection for Cop 2</h2>
        <select value={cop2City} onChange={(e) => handleCop2CitySelection}>
          <option value="">Select City</option>
          {getCitiesForCop2().map((city) => (
            <option key={city} value={city}>
              {city}
            </option>
          ))}
        </select>

        <h2>City Selection for Cop 3</h2>
        <select value={cop3City} onChange={(e) => handleCop3CitySelection}>
          <option value="">Select City</option>
          {getCitiesForCop3().map((city) => (
            <option key={city} value={city}>
              {city}
            </option>
          ))}
        </select>
        <button type="submit">Capture</button>
      </form>
    </div>
  );
};
